	//短文编辑页面-计算正文区域高度
	var hEdit =  $(window).height() - 160 ;
	$('.article-edit-container .edit-content').height(hEdit);
	$(window).resize(function(e) {
        var hEdit =  $(window).height() - 160 ;
		$('.article-edit-container .edit-content').height(hEdit);
    });
	
	
	
	//计算表情的宽度
	$('.face-module-row>a').height($('.face-module-row>a').width());
	
	
	$('.loadpic-ok img').each(function(index, element) {
        if($(this).width() < $(this).height()){
			$(this).width('3.5rem').height('auto').css('margin-top','-50%');
		}
		else if($(this).width() > $(this).height()){
			$(this).height('3.5rem').width('auto').css('margin-left','-50%');
		}
		
});


	
